from flask import Flask, redirect, url_for, request, render_templatefrom faker import Fakerfrom random import randintfake = Faker(['en-US'])fake = Faker()app = Flask(__name__)@app.route("/")def hello_world():    return "<p>Hello, World!</p>"@app.route('/requirements')def req():    with open('requirements.txt') as f:        res = f.read()    return res@app.route('/urls/')def query_example():    # if key doesn't exist, returns None    language = request.args.get('language')    # if key doesn't exist, returns a 400, bad request error    framework = request.args['framework']    # if key doesn't exist, returns None    website = request.args.get('website')    return '''              <h1>The language value is: {}</h1>              <h1>The framework value is: {}</h1>              <h1>The website value is: {}'''.format(language, framework, website)if __name__ == '__main__':   app.run(debug = True)